  const form = useRef();
  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_9v2gipl', 
    'template_6sicgx7', 
    form.current, 
    'jysvNywN0d2HeSXN1'
    ).then((result) => {
      console.log(result.text);
    }, (error) => {
      console.log(error.text);
    });
    e.target.reset();
  }

    return (
